# -*- coding: utf-8 -*-# In python2 print è uno statement, utilizziamo la print di python3# che è una funzione per poter definire un endline diversofrom __future__ import print_functionimport tracebackdebug = True#############################################def log(msg, end='\n'): # a default stampa a fine stringa il newline    print('-L- %s' % msg, end=end)#############################################    def log_exc(e, msg):    print('-E- %s: %s' % (e.__class__.__name__, msg))    if debug:        traceback.print_exc()